function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if(! newValue==''){
		g_form.setSectionDisplay('device_details',true);g_form.setSectionDisplay('delivery_details',true);

	g_form.getReference('u_reference_8',getDevDetail);
	}
	function getDevDetail(DevDetail){
		g_form.setValue('u_string_2',DevDetail.model_number);
		g_form.setValue('u_price_5', DevDetail.u_price_1);
		g_form.setValue('u_string_4',DevDetail.short_description);
		g_form.setValue('u_glide_date_6', DevDetail.warranty_expiration);
	}

   //Type appropriate comment here, and begin script below

}
